marketing_links = get_articles_list

def build_title
	prompts = ['Interested in','researching','Looking into', 'Looking for feedback on', 
		'Looking for sellers on','Do you use', 'Have you used','Recommendation','Field report','User report',
		'New online service','New cannabis directory','Directory of local cannabis sellers','Index of cannabis sellers',
		'Sell your weed on','You can list your cannabis business','You can sell cannabis and paraphernalia on',
		'good-weed - the new directory for cannabis sellers',
		'good-weed - an index cannabis sellers',
		"How do I find GOOD weed? good-weed?",
		"Where do I find the best weed? good-weed?",
		"Is good-weed.com the best place to find the best cannabis?",
		"Is there a way to promote my profile on good-weed?",
		"How do I optimize my good-weed account?",
		"Can I get some traction on my good-weed account?",		
		"Tips for indie licensed cannabis seller on good-weed?",		
	]

	prompts.sample + ' (good-weed.com)'
end

def build_text
	hi_texts = ['Hi','Hello','Hey there',"What's up"]
	audience = ['guys','folks','friends','smokers','stoners','potheads','cannabis enthusiasts','weed lovers','everybody','people','dudes and dudettes']
	story    = [
		"I'm looking to promote my cannabis service on good-weed.com. Any tips?",
		"Has anyone tried selling weed on good-weed.com?",
		"A friend recommended good-weed.com. Has anyone tried selling or buying cannabis on it?",
		"Does anyone want to list their cannabis service on good-weed.com?",
		"Is anyone experienced with good-weed.com?",
		"I am learning more about the local cannabis business - should I promote my business at good-weed.com?",
		"What direction is the cannabis industry moving in? What do you think about good-weed.com, for example?",
		"How does good-weed.com compare to other such services?",
		"Should I list my legal cannabis business on good-weed.com?",
		"I'm really impressed by good-weed.com. What do you guys think of it?",
		"Is good-weed.com a game-changer for licensed indie cannabis sellers?",
		"What do you guys think of this? ",
	]

	hi_texts.sample+', '+audience.sample+'; '+story.sample
end

get '/marketing' do 

	first_name = female_names.sample
	last_name  = $last_names.sample.capitalize
	name  =  first_name + ' ' + last_name
	email =  first_name[0].downcase+'.'+last_name.downcase+'@gmail.com'
	profile_pic   =  profile_pics.sample 
	weed_pic      =  $weed_pics.sample

	idx   = Date.today.day
	links = marketing_links
	link  = $root_url + '/' + links[idx % links.size][:link]
	text  = build_text
	{
		name: name,
		email: email,
		profile_pic: profile_pic,
		weed_pic: weed_pic,		
		title: build_title,
		text: text,
		link: link,		
		# all_links: links,	
	}
	res = "
	name: #{name} <br/>
	email: #{email} <br/>
	profile_pic: #{profile_pic} <br/>
	title: #{build_title} <br/>
	text: #{text} <br/>
	link: #{link} <br/>

	"
	res
end